    elif rating >= 3.5:
        return base_amount * 0.10
    else:
        return base_amount * 0.05

def calculate_tenure_bonus(base_amount: float, years: int) -> float:
    """Calculate bonus based on years of service."""
    if years >= 10:
        return base_amount * 0.05
    elif years >= 5:
        return base_amount * 0.03
    return 0.0

def calculate_bonus(params: BonusParams) -> float:
    """Calculate total bonus from performance and tenure."""
    performance_bonus = calculate_performance_bonus(
        params.base_amount,
        params.rating
    )
    tenure_bonus = calculate_tenure_bonus(
        params.base_amount,
        params.years
    )
    return performance_bonus + tenure_bonus

def calculate_employee_bonus(employee) -> float:
