async fn fetch_api_resource<T: DeserializeOwned>(
    resource: &str,
    id: i64,
) -> Result<T, Error> {
    let url = format!("https://api.example.com/{}/{}", resource, id);
    let response = reqwest::get(&url).await?;

    if !response.status().is_success() {
        return Err(Error::ApiError(response.status()));
