    db *sql.DB
}

func (r *OrderRepository) Save(order Order) error {
    _, err := r.db.Exec(
        "INSERT INTO orders (total, tax) VALUES (?, ?)",
        order.Total, order.TaxAmount,
    )
    return err
}

// Email notifications
