    smtpHost string
    from     string
}

func (e *EmailService) SendOrderConfirmation(order Order) error {
    body := fmt.Sprintf("Order total: $%.2f", order.Total+order.TaxAmount)
    return smtp.SendMail(
        e.smtpHost,
        nil,
        e.from,
        []string{order.CustomerEmail},
        []byte(body),
    )
}

