from abc import ABC, abstractmethod
import json
from datetime import datetime

class LogFormatter(ABC):
    @abstractmethod
    def format(self, message: str) -> str:
        pass

class TextFormatter(LogFormatter):
    def format(self, message: str) -> str:
        return f"[LOG] {message}"

