function parseUserData(data: string): User {
    let parsed: any;

    // Parse JSON with helpful error
    try {
        parsed = JSON.parse(data);
    } catch (e) {
        throw new UserParseError(
            `Invalid JSON format: ${(e as Error).message}`,
            data
        );
    }

    // Validate fields with specific errors
    if (!parsed.id) {
        throw new UserParseError(
            'Missing required field: id',
            data
        );
    }

    if (!parsed.email) {
        throw new UserParseError(
            'Missing required field: email',
            data
        );
    }

    if (!parsed.age) {
        throw new UserParseError(
            'Missing required field: age',
            data
        );
    }

    // Additional validation
    if (typeof parsed.age !== 'number') {
        throw new UserParseError(
            `Invalid type for age: expected number, got ${typeof parsed.age}`,
            data
        );
    }

    return parsed as User;
}

