 * @param options.onProgress - Callback for progress updates
 * 
 * @returns Array of all results
 * 
 * @example
 * ```typescript
 * // Process 10,000 users in batches of 100
 * const results = await processBatch(
 *   users,
 *   100,
 *   async (batch) => {
 *     return await db.users.insertMany(batch);
 *   },
 *   {
 *     concurrency: 5,
 *     onProgress: (processed, total) => {
 *       console.log(`${processed}/${total} processed`);
 *     }
 *   }
 * );
 * ```
 */
async function processBatch<T, R>(
  items: T[],
  batchSize: number = 100,
  processor: (batch: T[]) => Promise<R>,
  options: BatchOptions = {}
): Promise<R[]> {
  const { concurrency = 1, onProgress } = options;
  const results: R[] = [];

  // Split into batches
  const batches: T[][] = [];
  for (let i = 0; i < items.length; i += batchSize) {
    batches.push(items.slice(i, i + batchSize));
  }

  // Process with concurrency control
  let processed = 0;
  for (let i = 0; i < batches.length; i += concurrency) {
    const batchGroup = batches.slice(i, i + concurrency);
    const batchResults = await Promise.all(
      batchGroup.map(batch => processor(batch))
    );

    results.push(...batchResults);
    processed += batchGroup.length * batchSize;

    if (onProgress) {
      onProgress(Math.min(processed, items.length), items.length);
    }
  }

  return results;
}
