I'll implement privacy-respecting telemetry with OpenTelemetry.

```typescript
import { trace, context, SpanStatusCode } from '@opentelemetry/api';

const tracer = trace.getTracer('app-name', '1.0.0');

/**
 * Track feature usage with OpenTelemetry
 * 
 * Privacy considerations:
 * - No PII (personally identifiable information)
 * - Aggregated metrics only
 * - Opt-out via TELEMETRY_ENABLED=false
 * 
 * @param featureName - Name of feature being used
 * @param metadata - Additional context (non-PII only)
 * 
 * @example
 * ```typescript
 * trackFeatureUsage('export_pdf', {
 *   format: 'A4',
 *   pages: 10,
 *   //  Don't include: userId, email, fileName
 * });
 * ```
 */
function trackFeatureUsage(
  featureName: string,
  metadata: Record<string, string | number> = {}
): void {
  if (!process.env.TELEMETRY_ENABLED) {
    return; // Respect opt-out
  }

  const span = tracer.startSpan(`feature.${featureName}`, {
    attributes: {
      'feature.name': featureName,
      ...metadata,
    },
  });

  span.end();
}

/**
 * Track performance metrics for operations
 * 
 * Automatically measures duration and records errors.
 * 
 * @param operationName - Name of operation
 * @param fn - Async function to measure
 * @returns Result from fn
 * 
 * @example
 * ```typescript
 * const results = await trackOperation('database.query', async () => {
 *   return await db.query('SELECT * FROM users');
 * });
 * ```
 */
async function trackOperation<T>(
  operationName: string,
  fn: () => Promise<T>
): Promise<T> {
  const span = tracer.startSpan(operationName);

  try {
    const result = await fn();
    span.setStatus({ code: SpanStatusCode.OK });
    return result;
  } catch (error) {
    // Track error without exposing sensitive data
    span.setStatus({
      code: SpanStatusCode.ERROR,
      message: error.message,
    });
    span.recordException(error);
    throw error;
  } finally {
    span.end();
  }
}

// Example usage
app.post('/api/export', async (req, res) => {
  // Track feature usage (no PII)
  trackFeatureUsage('export_pdf', {
    format: req.body.format,
    pages: req.body.pageCount,
  });

  // Track performance
  const pdf = await trackOperation('export.generate_pdf', async () => {
    return await generatePDF(req.body);
  });

  res.send(pdf);
});
