```typescript
import { z } from 'zod';

/**
 * Application configuration schema
 * 
 * Uses Zod for runtime validation and type safety.
 * Environment variables are validated on startup,
 * failing fast if configuration is invalid.
 * 
 * @example
 * ```typescript
 * // Access config anywhere in app
 * const dbUrl = config.database.url;
 * const apiKey = config.stripe.apiKey;
 * ```
 */
const configSchema = z.object({
  // Server configuration
  server: z.object({
    port: z.coerce.number().int().min(1).max(65535).default(3000),
    host: z.string().default('localhost'),
    nodeEnv: z.enum(['development', 'production', 'test']).default('development'),
  }),

  // Database configuration
  database: z.object({
    url: z.string().url(),
    poolSize: z.coerce.number().int().min(1).max(100).default(10),
    // Allow undefined in development (uses SQLite)
    ssl: z.boolean().optional(),
  }),

  // Redis configuration
  redis: z.object({
    url: z.string().url(),
    password: z.string().optional(),
  }),

  // External services
  stripe: z.object({
    apiKey: z.string().min(1),
    webhookSecret: z.string().min(1),
  }),

  // Feature flags
  features: z.object({
    enableTelemetry: z.coerce.boolean().default(true),
    enableRateLimiting: z.coerce.boolean().default(true),
  }),

  // Logging
  logging: z.object({
    level: z.enum(['debug', 'info', 'warn', 'error']).default('info'),
  }),
});

/**
 * Load and validate configuration from environment variables
 * 
 * Reads from process.env, validates against schema, and provides
 * type-safe access to configuration values.
 * 
 * @throws {Error} If required env vars are missing or invalid
 * @returns Validated configuration object
 */
function loadConfig() {
  try {
    const config = configSchema.parse({
      server: {
        port: process.env.PORT,
        host: process.env.HOST,
        nodeEnv: process.env.NODE_ENV,
      },
      database: {
        url: process.env.DATABASE_URL,
        poolSize: process.env.DB_POOL_SIZE,
        ssl: process.env.DB_SSL === 'true',
      },
      redis: {
        url: process.env.REDIS_URL,
        password: process.env.REDIS_PASSWORD,
      },
      stripe: {
        apiKey: process.env.STRIPE_API_KEY,
        webhookSecret: process.env.STRIPE_WEBHOOK_SECRET,
      },
      features: {
        enableTelemetry: process.env.ENABLE_TELEMETRY,
        enableRateLimiting: process.env.ENABLE_RATE_LIMITING,
      },
      logging: {
        level: process.env.LOG_LEVEL,
      },
    });

    return config;
  } catch (error) {
    if (error instanceof z.ZodError) {
      // Provide helpful error messages
      console.error('Configuration validation failed:');
      error.errors.forEach(err => {
        console.error(`  - ${err.path.join('.')}: ${err.message}`);
      });
      process.exit(1);
    }
    throw error;
  }
}

// Export singleton config instance
export const config = loadConfig();
export type Config = z.infer<typeof configSchema>;
