describe('Authentication', () => {
  let auth: Auth;

  beforeEach(async () => {
    // Fresh database for each test
    await db.migrate.latest();
    auth = new Auth(db);
  });

  afterEach(async () => {
    // Clean up database
    await db.migrate.rollback();
  });

  describe('User Registration', () => {
    it('should create user with valid credentials', async () => {
      const user = await auth.register({
        username: 'testuser',
        email: 'test@example.com',
        password: 'SecurePass123!',
      });

      expect(user).toHaveProperty('id');
      expect(user.username).toBe('testuser');
      expect(user.email).toBe('test@example.com');
      // Password should NOT be in response
      expect(user).not.toHaveProperty('password');
    });

    it('should hash password before storage', async () => {
      const password = 'SecurePass123!';
      await auth.register({
        username: 'testuser',
        email: 'test@example.com',
        password,
      });

      // Check that password is hashed in database
      const dbUser = await db.query('SELECT password FROM users WHERE username = ?', ['testuser']);

      // Hashed password should NOT equal plaintext
      expect(dbUser.password).not.toBe(password);
      // Should use bcrypt format ($2b$...)
      expect(dbUser.password).toMatch(/^\$2b\$/);
    });

    it('should reject weak passwords', async () => {
      await expect(
        auth.register({
          username: 'testuser',
          email: 'test@example.com',
          password: 'weak', // Too short, no special chars
        })
      ).rejects.toThrow('Password must be at least 8 characters');
    });

    it('should reject duplicate usernames', async () => {
      await auth.register({
        username: 'testuser',
        email: 'test1@example.com',
        password: 'SecurePass123!',
      });

      await expect(
        auth.register({
          username: 'testuser', // Duplicate!
          email: 'test2@example.com',
          password: 'SecurePass123!',
        })
      ).rejects.toThrow('Username already exists');
    });

    it('should reject invalid email format', async () => {
      await expect(
        auth.register({
          username: 'testuser',
          email: 'not-an-email',
          password: 'SecurePass123!',
        })
      ).rejects.toThrow('Invalid email format');
