      }

      // 6th attempt should be rate limited
      await expect(
        auth.login('testuser', 'SecurePass123!') // Even correct password!
      ).rejects.toThrow('Too many failed attempts');
    });
  });

  describe('Token Validation', () => {
    let validToken: string;

    beforeEach(async () => {
      await auth.register({
        username: 'testuser',
        email: 'test@example.com',
        password: 'SecurePass123!',
      });

      const result = await auth.login('testuser', 'SecurePass123!');
      validToken = result.token;
    });

    it('should accept valid token', async () => {
      const decoded = await auth.verifyToken(validToken);
      expect(decoded.username).toBe('testuser');
    });

    it('should reject expired token', async () => {
      // Create token that expires immediately
      const expiredToken = auth.generateToken('testuser', { expiresIn: '0s' });

      await expect(
        auth.verifyToken(expiredToken)
      ).rejects.toThrow('Token expired');
    });

    it('should reject tampered token', async () => {
      // Modify token (change last character)
      const tamperedToken = validToken.slice(0, -1) + 'X';

      await expect(
        auth.verifyToken(tamperedToken)
      ).rejects.toThrow('Invalid token');
    });
  });
});
