```javascript
// webpack.config.js
const path = require('path');
const { BundleAnalyzerPlugin } = require('webpack-bundle-analyzer');
const TerserPlugin = require('terser-webpack-plugin');
const CompressionPlugin = require('compression-webpack-plugin');

/**
 * Production webpack configuration
 * 
 * Optimizations:
 * 1. Code splitting: Separate vendor/app bundles
 * 2. Tree shaking: Remove unused exports
 * 3. Minification: Terser with aggressive settings
 * 4. Compression: Gzip/Brotli for static assets
 * 5. Source maps: Lightweight in production
 * 
 * Bundle size targets:
 * - Main bundle: < 200 KB
 * - Vendor bundle: < 300 KB
 * - Total (gzipped): < 150 KB
 */
module.exports = {
  mode: 'production',

  entry: {
    app: './src/index.ts',
  },

  output: {
    path: path.resolve(__dirname, 'dist'),
    filename: '[name].[contenthash].js',
    chunkFilename: '[name].[contenthash].chunk.js',
    clean: true, // Remove old bundles
  },

  optimization: {
    minimize: true,
    minimizer: [
      new TerserPlugin({
        terserOptions: {
          compress: {
            // Remove console.log in production
            drop_console: true,
            // Remove debugger statements
            drop_debugger: true,
            // Aggressive optimization
            passes: 2,
          },
          mangle: true, // Shorten variable names
          output: {
            comments: false, // Remove comments
          },
        },
        extractComments: false,
      }),
    ],

    // Split vendor code into separate bundle
    // Vendors change less frequently  better caching
    splitChunks: {
      chunks: 'all',
      cacheGroups: {
        vendor: {
          test: /[\\/]node_modules[\\/]/,
          name: 'vendors',
          priority: 10,
        },
        common: {
          minChunks: 2,
          priority: 5,
          reuseExistingChunk: true,
        },
      },
    },

    // Extract runtime into separate file
    // Prevents vendor bundle invalidation on app changes
    runtimeChunk: 'single',
  },

  module: {
    rules: [
      {
        test: /\.(ts|tsx)$/,
        use: {
          loader: 'ts-loader',
          options: {
            // Faster builds, type checking in separate process
            transpileOnly: true,
          },
        },
        exclude: /node_modules/,
      },
      {
        test: /\.css$/,
        use: [
          'style-loader',
          {
            loader: 'css-loader',
            options: {
              // Enable CSS modules for scoped styles
              modules: true,
            },
          },
        ],
      },
    ],
  },

  plugins: [
    // Analyze bundle size (run with ANALYZE=true)
    process.env.ANALYZE &&
      new BundleAnalyzerPlugin({
        analyzerMode: 'static',
        reportFilename: 'bundle-report.html',
        openAnalyzer: true,
      }),

    // Generate gzip files for static assets
    new CompressionPlugin({
      algorithm: 'gzip',
      test: /\.(js|css|html|svg)$/,
      threshold: 10240, // Only compress files > 10KB
      minRatio: 0.8,
    }),

    // Generate brotli files (better compression than gzip)
    new CompressionPlugin({
      algorithm: 'brotliCompress',
      test: /\.(js|css|html|svg)$/,
      threshold: 10240,
      minRatio: 0.8,
      filename: '[path][base].br',
    }),
  ].filter(Boolean),

  // Lightweight source maps for production debugging
  // Full source maps are too large (adds MBs)
  devtool: 'source-map',

  // Performance hints
  performance: {
    hints: 'error',
    maxEntrypointSize: 250000, // 250 KB
    maxAssetSize: 300000, // 300 KB
  },
};
