```typescript
// knexfile.ts
import { Knex } from 'knex';
import * as path from 'path';

/**
 * Knex.js configuration for database migrations
 * 
 * Migrations enable:
 * - Version-controlled schema changes
 * - Safe rollbacks
 * - Team collaboration (no manual SQL scripts)
 * - Environment-specific configs (dev/staging/prod)
 * 
 * Directory structure:
 * migrations/
 *   20240101000000_create_users_table.ts
 *   20240101000001_add_email_index.ts
 *   20240102000000_create_orders_table.ts
 */
const config: { [key: string]: Knex.Config } = {
  development: {
    client: 'postgresql',
    connection: {
      host: process.env.DB_HOST || 'localhost',
      port: parseInt(process.env.DB_PORT || '5432'),
      database: process.env.DB_NAME || 'myapp_dev',
      user: process.env.DB_USER || 'postgres',
      password: process.env.DB_PASSWORD || 'postgres',
    },
    migrations: {
      directory: path.join(__dirname, 'migrations'),
      extension: 'ts',
      tableName: 'knex_migrations',
    },
    seeds: {
      directory: path.join(__dirname, 'seeds'),
      extension: 'ts',
    },
  },

  production: {
    client: 'postgresql',
    connection: process.env.DATABASE_URL,
    pool: {
      min: 2,
      max: 10,
    },
    migrations: {
      directory: path.join(__dirname, 'migrations'),
      extension: 'ts',
      tableName: 'knex_migrations',
    },
    // No seeds in production!
  },
};

export default config;
```

Example migration:
```typescript
// migrations/20240101000000_create_users_table.ts
import { Knex } from 'knex';

/**
 * Create users table
 * 
 * Schema:
 * - id: UUID primary key
 * - username: Unique, indexed
 * - email: Unique, indexed
 * - password_hash: Bcrypt hash
 * - created_at/updated_at: Timestamps
 */
export async function up(knex: Knex): Promise<void> {
  await knex.schema.createTable('users', (table) => {
    table.uuid('id').primary().defaultTo(knex.raw('gen_random_uuid()'));
    table.string('username', 50).notNullable().unique();
    table.string('email', 255).notNullable().unique();
    table.string('password_hash', 255).notNullable();
    table.timestamp('created_at').defaultTo(knex.fn.now());
    table.timestamp('updated_at').defaultTo(knex.fn.now());

    // Indexes for fast lookups
    table.index('username');
    table.index('email');
    table.index('created_at'); // For sorting/filtering
  });

  // Add trigger for updated_at auto-update
  await knex.raw(`
    CREATE OR REPLACE FUNCTION update_updated_at_column()
    RETURNS TRIGGER AS $$
    BEGIN
      NEW.updated_at = NOW();
      RETURN NEW;
    END;
    $$ LANGUAGE plpgsql;

    CREATE TRIGGER update_users_updated_at
    BEFORE UPDATE ON users
    FOR EACH ROW
    EXECUTE FUNCTION update_updated_at_column();
  `);
}

export async function down(knex: Knex): Promise<void> {
  // Drop trigger first, then table
  await knex.raw('DROP TRIGGER IF EXISTS update_users_updated_at ON users');
  await knex.raw('DROP FUNCTION IF EXISTS update_updated_at_column');
  await knex.schema.dropTableIfExists('users');
}
```

Example migration with data:
```typescript
// migrations/20240101000001_add_email_verification.ts
import { Knex } from 'knex';

/**
 * Add email verification fields
 * 
 * Changes:
 * - Add email_verified boolean (default false)
 * - Add email_verification_token (nullable)
 * - Add email_verified_at timestamp (nullable)
 * 
 * Migration strategy:
 * - New columns default to NULL/false
 * - Existing users marked as verified (grandfathered in)
 */
export async function up(knex: Knex): Promise<void> {
  await knex.schema.alterTable('users', (table) => {
    table.boolean('email_verified').defaultTo(false).notNullable();
    table.string('email_verification_token', 64).nullable();
    table.timestamp('email_verified_at').nullable();

    // Index for token lookup
    table.index('email_verification_token');
  });

  // Mark existing users as verified (grandfather clause)
  await knex('users').update({
    email_verified: true,
    email_verified_at: knex.fn.now(),
  });
}

export async function down(knex: Knex): Promise<void> {
  await knex.schema.alterTable('users', (table) => {
    table.dropColumn('email_verified');
    table.dropColumn('email_verification_token');
    table.dropColumn('email_verified_at');
  });
}
