  // ✅ SAFE: React escapes {text} and {author} automatically
  return (
    <div className="comment">
      <strong>{author}</strong>: {text}
    </div>
  );
}

// ❌ UNSAFE: Never use dangerouslySetInnerHTML without sanitization
