// ✅ SAFE: Only use with DOMPurify
import DOMPurify from 'dompurify';
function RichComment({ html }: { html: string }) {
  return (
    <div dangerouslySetInnerHTML={{
      __html: DOMPurify.sanitize(html)
    }} />
  );
}
